package com.expd.arch.messaging.router.chart;import java.awt.event.MouseEvent;import java.awt.event.MouseListener;public class MouseEar implements MouseListener {    private RouterThroughputChart throughputChart;    public MouseEar(RouterThroughputChart throughputChart) {        this.throughputChart = throughputChart;    }    public void mouseClicked(MouseEvent e) {        if (this.throughputChart != null) {            this.throughputChart.setChartValue(0.0);            System.out.println("Reset RouterThroughputChart to 0.0");        }    }    public void mouseEntered(MouseEvent e) {    }    public void mouseExited(MouseEvent e) {    }    public void mousePressed(MouseEvent e) {    }    public void mouseReleased(MouseEvent e) {    }}